var mongoose = require ('mongoose');

/**
 * Factory function for creating the 'oauth2_client' schema.
 */
function create_schema () {
  var Schema = mongoose.Schema;

  var schema = new Schema ({
    name : {type: String, trim: true},
    secret : {type: String},
    redirect_uri : {type: String, required: true},
    disabled : {type: Boolean, default: false}
  });

  return schema;  
}

const COLLECTION_NAME = 'oauth2_client';
var model;

try {
  // There is no need to create a schema if it already exists.
  if (mongoose.model (COLLECTION_NAME)) 
    model = mongoose.model (COLLECTION_NAME);
} 
catch (e) {
  if (e.name === 'MissingSchemaError') {
    var schema = create_schema ();
    model = mongoose.model (COLLECTION_NAME, schema);
  }
}

module.exports = exports = model;
